mapper.select:
SELECT R.AMOUNT ,A.NAME AS AMOUNT_NAME ,I.NAME AS ING_NAME ,I.EXPLANATION ,I.SRC AS ING_SRC ,NVL(I.ALC,0) AS ALC ,NVL(B.BASE_NAME,'없음') AS BASE_NAME ,IC.CATEGORY AS ING_CATEGORY_NAME FROM RECIPE R JOIN AMOUNT A ON R.AMOUNT_NO = A.NO JOIN INGREDIENT I ON R.ING_NO = I.NO LEFT JOIN BASE B ON I.BASE_NO = B.NO LEFT JOIN ING_CATEGORY IC ON I.CATEGORY_NO = IC.NO
AND R.COCKTAIL_NO = #{cocktailNo}
AND I.NO = #{ingNo}
ORDER BY I.NO

SELECT I.NAME AS ING_NAME ,I.EXPLANATION ,I.SRC AS ING_SRC ,NVL(I.ALC,0) AS ALC ,NVL(B.BASE_NAME,'없음') AS BASE_NAME ,IC.CATEGORY AS ING_CATEGORY_NAME FROM INGREDIENT I LEFT JOIN BASE B ON I.BASE_NO = B.NO LEFT JOIN ING_CATEGORY IC ON I.CATEGORY_NO = IC.NO
AND I.NO = #{ingNo}
ORDER BY I.NO

SELECT I.NO AS ING_NO ,I.NAME AS ING_NAME ,I.EXPLANATION ,I.SRC AS ING_SRC ,IC.CATEGORY AS ING_CATEGORY_NAME FROM INGREDIENT I LEFT JOIN ING_CATEGORY IC ON I.CATEGORY_NO = IC.NO
AND IC.CATEGORY = #{ingCategoryName}
ORDER BY I.NO
