mapper.insert:
INSERT INTO MEMBER
(
NO
,ID
,PWD
,NICK
,EMAIL
)
VALUES
(
SEQ_MEMBER_NO.NEXTVAL
, #{id}
, #{pwd}
, #{nick}
, #{email}
)

mapper.update:
UPDATE MEMBER
SET
 PWD=#{pwd}
, NICK=#{nick}
,EDIT_DATE=CURRENT_TIMESTAMP
WHERE NO=#{no}
AND QUIT_YN='N'

mapper.select:
SELECT 
COUNT(*)
FROM MEMBER
WHERE NO=#{no}
AND PWD=#{pwd}
